class Day4 {
    func doWork(lines: [String] = lines2) {
        var result = 0
        
        var chars: [[String]] = lines.map{ $0.map { String($0) } }
        
        let deltas: [(Int, Int)] = [
            (-1, -1), (00, -1), (+1, -1),
            (-1, 00), (00, 00), (+1, 00),
            (-1, +1), (00, +1), (+1, +1),
        ]

        for (i, line) in lines.enumerated() {
            for (j, ch) in line.enumerated() where ch == "@" {
                var tmp = 0

                for d in deltas {
                    let ii = d.0 + i
                    let jj = d.1 + j

                    if ii == i && jj == j { continue }
                    
                    if ii < 0 || ii >= lines.count || jj < 0 || jj >= line.count {
                        continue
                    }
                    
                    let nCh = String(lines[ii][line.index(line.startIndex, offsetBy: jj)])
                    if nCh == "@" {
                        tmp += 1
                    }
                }
                                
                if tmp < 4 {
                    chars[i][j] = "x"
                    result += 1
                }
            }
        }
        
        print(result)
     
//        for line in chars {
//            print(line.joined())
//        }
    }
}
